import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import DataTable from '../components/DataTable';
import UserProfileDropdown from '../components/UserProfileDropdown';

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [farmers, setFarmers] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [registrations, setRegistrations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    status: '',
    role: '',
    designation: '',
    state: '',
    district: ''
  });
  const [registrationFilters, setRegistrationFilters] = useState({
    status: '',
    role: '',
    state: '',
    district: ''
  });
  const [employeeFilters, setEmployeeFilters] = useState({
    status: '',
    role: '',
    designation: '',
    state: '',
    district: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const [farmersResponse, employeesResponse, registrationsResponse] = await Promise.allSettled([
        axios.get('/api/farmers', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('/api/employees', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('/api/registrations', { headers: { Authorization: `Bearer ${token}` } })
      ]);

      setFarmers(farmersResponse.status === 'fulfilled' ? farmersResponse.value.data : []);
      setEmployees(employeesResponse.status === 'fulfilled' ? employeesResponse.value.data : []);
      setRegistrations(registrationsResponse.status === 'fulfilled' ? registrationsResponse.value.data : []);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const getStats = () => {
    const totalFarmers = farmers.length;
    const totalEmployees = employees.length;
    const totalFPO = 0; // Placeholder for FPO count

    const farmersChange = '+12.4%';
    const employeesChange = '-3.0%';
    const fpoChange = '+0.0%';

    return {
      farmers: { count: totalFarmers, change: farmersChange },
      employees: { count: totalEmployees, change: employeesChange },
      fpo: { count: totalFPO, change: fpoChange }
    };
  };

  const getFilteredFarmers = () => {
    return farmers.filter(farmer => {
      const matchesStatus = !filters.status || farmer.kycStatus?.toLowerCase() === filters.status.toLowerCase();
      const matchesRole = !filters.role || farmer.role?.toLowerCase() === filters.role.toLowerCase();
      const matchesState = !filters.state || farmer.state?.toLowerCase() === filters.state.toLowerCase();
      const matchesDistrict = !filters.district || farmer.district?.toLowerCase() === filters.district.toLowerCase();

      return matchesStatus && matchesRole && matchesState && matchesDistrict;
    });
  };

  const getFilteredEmployees = () => {
    return employees.filter(employee => {
      const matchesStatus = !employeeFilters.status || employee.status?.toLowerCase() === employeeFilters.status.toLowerCase();
      const matchesRole = !employeeFilters.role || employee.role?.toLowerCase() === employeeFilters.role.toLowerCase();
      const matchesDesignation = !employeeFilters.designation || employee.designation?.toLowerCase() === employeeFilters.designation.toLowerCase();
      const matchesState = !employeeFilters.state || employee.state?.toLowerCase() === employeeFilters.state.toLowerCase();
      const matchesDistrict = !employeeFilters.district || employee.district?.toLowerCase() === employeeFilters.district.toLowerCase();

      return matchesStatus && matchesRole && matchesDesignation && matchesState && matchesDistrict;
    });
  };

  const getFilteredRegistrations = () => {
    return registrations.filter(registration => {
      const matchesStatus = !registrationFilters.status || registration.status?.toLowerCase() === registrationFilters.status.toLowerCase();
      const matchesRole = !registrationFilters.role || registration.role?.toLowerCase() === registrationFilters.role.toLowerCase();
      const matchesState = !registrationFilters.state || registration.state?.toLowerCase() === registrationFilters.state.toLowerCase();
      const matchesDistrict = !registrationFilters.district || registration.district?.toLowerCase() === registrationFilters.district.toLowerCase();

      return matchesStatus && matchesRole && matchesState && matchesDistrict;
    });
  };

  const handleApproveKYC = (farmerId) => {
    console.log('Approving KYC for farmer:', farmerId);
    // Implement KYC approval logic
  };

  const handleRejectKYC = (farmerId) => {
    console.log('Rejecting KYC for farmer:', farmerId);
    // Implement KYC rejection logic
  };

  const handleApproveRegistration = (registrationId) => {
    console.log('Approving registration:', registrationId);
    // Implement registration approval logic
  };

  const handleRejectRegistration = (registrationId) => {
    console.log('Rejecting registration:', registrationId);
    // Implement registration rejection logic
  };

  const stats = getStats();

  const renderOverview = () => (
    <>
      {/* Welcome Section */}
      <div className="welcome-section">
        <h1 className="welcome-title">Admin Dashboard</h1>
        <p className="welcome-subtitle">Welcome back! Here's what's happening with your agricultural data.</p>
      </div>

      {/* Time Filters */}
      <div className="time-filters">
        <button className="time-filter-btn">
          <i className="fas fa-sync-alt"></i>
          Refresh
        </button>
        <button className="time-filter-btn active">
          <i className="fas fa-calendar-day"></i>
          Today
        </button>
        <button className="time-filter-btn">
          <i className="fas fa-calendar-week"></i>
          This Month
        </button>
        <button className="time-filter-btn">
          <i className="fas fa-calendar"></i>
          This Year
        </button>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stats-card">
          <div className="stats-icon farmers">
            <i className="fas fa-users"></i>
          </div>
          <div className="stats-title">Farmers</div>
          <div className="stats-value">{stats.farmers.count}</div>
          <div className={`stats-change ${stats.farmers.change.startsWith('+') ? 'positive' : 'negative'}`}>
            <i className={`fas fa-${stats.farmers.change.startsWith('+') ? 'arrow-up' : 'arrow-down'}`}></i>
            {stats.farmers.change}
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-icon employees">
            <i className="fas fa-user-tie"></i>
          </div>
          <div className="stats-title">Employees</div>
          <div className="stats-value">{stats.employees.count}</div>
          <div className={`stats-change ${stats.employees.change.startsWith('+') ? 'positive' : 'negative'}`}>
            <i className={`fas fa-${stats.employees.change.startsWith('+') ? 'arrow-up' : 'arrow-down'}`}></i>
            {stats.employees.change}
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-icon fpo">
            <i className="fas fa-building"></i>
          </div>
          <div className="stats-title">FPO</div>
          <div className="stats-value">{stats.fpo.count}</div>
          <div className={`stats-change ${stats.fpo.change.startsWith('+') ? 'positive' : 'negative'}`}>
            <i className={`fas fa-${stats.fpo.change.startsWith('+') ? 'arrow-up' : 'arrow-down'}`}></i>
            {stats.fpo.change}
          </div>
        </div>
      </div>

      {/* Bottom Sections */}
      <div className="bottom-sections">
        {/* Recent Activities */}
        <div className="section-card">
          <div className="section-header">
            <h3 className="section-title">Recent Activities</h3>
            <a href="#" className="section-link">View All</a>
          </div>
          <div className="activities-list">
            <div className="activity-item">
              <div className="activity-icon success">
                <i className="fas fa-user-edit"></i>
              </div>
              <div className="activity-content">
                <div className="activity-text">Farmer profile updated</div>
                <div className="activity-time">20m ago</div>
              </div>
              <span className="activity-badge success">SUCCESS</span>
            </div>
            <div className="activity-item">
              <div className="activity-icon success">
                <i className="fas fa-user-tie"></i>
              </div>
              <div className="activity-content">
                <div className="activity-text">Employee profile updated</div>
                <div className="activity-time">10m ago</div>
              </div>
              <span className="activity-badge success">SUCCESS</span>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="section-card">
          <div className="section-header">
            <h3 className="section-title">Quick Actions</h3>
          </div>
          <div className="quick-actions-grid">
            <button className="quick-action-btn primary">
              <i className="fas fa-users"></i>
              View Users
            </button>
            <button className="quick-action-btn secondary">
              <i className="fas fa-user-friends"></i>
              View Farmers
            </button>
          </div>
        </div>
      </div>
    </>
  );

  const renderKYCOverview = () => (
    <div className="section-card">
      <div className="section-header">
        <h3 className="section-title">KYC Overview</h3>
      </div>
      <DataTable
        data={getFilteredFarmers()}
        columns={[
          { key: 'name', label: 'Name' },
          { key: 'phone', label: 'Phone' },
          { key: 'state', label: 'State' },
          { key: 'district', label: 'District' },
          { 
            key: 'kycStatus', 
            label: 'KYC Status',
            render: (value) => (
              <span className={`status-badge status-${value?.toLowerCase() || 'pending'}`}>
                {value || 'Pending'}
              </span>
            )
          }
        ]}
        customActions={(item) => [
          { label: 'Approve', onClick: () => handleApproveKYC(item.id), className: 'approve' },
          { label: 'Reject', onClick: () => handleRejectKYC(item.id), className: 'reject' }
        ]}
      />
    </div>
  );

  const renderFarmers = () => (
    <div className="section-card">
      <div className="section-header">
        <h3 className="section-title">Farmer Management</h3>
      </div>

      {/* Filters Section */}
      <div className="filters-section">
        <div className="filter-group">
          <label className="filter-label">Status</label>
          <select 
            className="filter-select"
            value={filters.status}
            onChange={(e) => setFilters({...filters, status: e.target.value})}
          >
            <option value="">All Status</option>
            <option value="approved">Approved</option>
            <option value="pending">Pending</option>
            <option value="rejected">Rejected</option>
          </select>
        </div>

