import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { farmersAPI, employeesAPI, superAdminAPI } from '../api/apiService';
import '../styles/Dashboard.css';
import FarmerForm from '../components/FarmerForm';
import EmployeeRegistrationForm from '../components/EmployeeRegistrationForm';
import AssignmentModal from '../components/AssignmentModal';
import KYCDocumentUpload from '../components/KYCDocumentUpload';
import ViewFarmerRegistrationDetails from '../components/ViewFarmerRegistrationDetails';
import ViewEditEmployeeDetails from '../components/ViewEditEmployeeDetails';
import StatsCard from '../components/StatsCard';
import DataTable from '../components/DataTable';
import UserProfileDropdown from '../components/UserProfileDropdown';
import DeleteModal from '../components/DeleteModal';
import RegistrationApprovalModal from '../components/RegistrationApprovalModal';
import RegistrationDetailModal from '../components/RegistrationDetailModal';

const SuperAdminDashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [farmers, setFarmers] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [showAssignmentModal, setShowAssignmentModal] = useState(false);
  const [showFarmerForm, setShowFarmerForm] = useState(false);
  const [showEmployeeForm, setShowEmployeeForm] = useState(false);
  const [showFarmerDetails, setShowFarmerDetails] = useState(false);
  const [selectedFarmerData, setSelectedFarmerData] = useState(null);
  const [showEmployeeDetails, setShowEmployeeDetails] = useState(false);
  const [selectedEmployeeData, setSelectedEmployeeData] = useState(null);
  const [showKYCDocumentUpload, setShowKYCDocumentUpload] = useState(false);
  const [selectedFarmerForKYC, setSelectedFarmerForKYC] = useState(null);
  const [editingFarmer, setEditingFarmer] = useState(null);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const [registrations, setRegistrations] = useState([]);
  const [showRegistrationModal, setShowRegistrationModal] = useState(false);
  const [selectedRegistration, setSelectedRegistration] = useState(null);
  const [showRegistrationDetailModal, setShowRegistrationDetailModal] = useState(false);
  const [selectedRegistrationForDetail, setSelectedRegistrationForDetail] = useState(null);
  const [registrationFilters, setRegistrationFilters] = useState({
    role: '',
    status: ''
  });
  const [filters, setFilters] = useState({
    state: '',
    district: '',
    region: '',
    kycStatus: '',
    assignmentStatus: '',
    employeeFilter: ''
  });

  // Greeting function based on time of day
  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour >= 5 && hour < 12) {
      return 'Good Morning';
    } else if (hour >= 12 && hour < 17) {
      return 'Good Afternoon';
    } else if (hour >= 17 && hour < 21) {
      return 'Good Evening';
    } else {
      return 'Good Night';
    }
  };

  // Load data from API
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      // Fetch farmers, employees, and registrations from API using super admin endpoints
      const farmersData = await superAdminAPI.getAllFarmers();
      const employeesData = await superAdminAPI.getAllEmployees();
      const registrationsData = await superAdminAPI.getAllRegistrations();

      if (farmersData && farmersData.length > 0) {
        setFarmers(farmersData);
      } else {
        loadMockData();
      }

      if (employeesData && employeesData.length > 0) {
        setEmployees(employeesData);
      } else {
        loadMockData();
      }

      if (registrationsData && registrationsData.length > 0) {
        setRegistrations(registrationsData);
      } else {
        loadMockRegistrationData();
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      loadMockData();
      loadMockRegistrationData();
    }
  };

  const loadMockData = () => {
    const mockFarmers = [
      {
        id: 1,
        name: 'John Farmer',
        contactNumber: '9876543210',
        email: 'john.farmer@example.com',
        state: 'Maharashtra',
        district: 'Pune',
        region: 'Western',
        kycStatus: 'PENDING',
        accessStatus: 'ACTIVE',
        assignedEmployee: null,
        assignedDate: null
      },
      {
        id: 2,
        name: 'Jane Farmer',
        contactNumber: '9876543211',
        email: 'jane.farmer@example.com',
        state: 'Gujarat',
        district: 'Ahmedabad',
        region: 'Western',
        kycStatus: 'APPROVED',
        accessStatus: 'ACTIVE',
        assignedEmployee: 'John Employee',
        assignedDate: '2024-01-15'
      },
      {
        id: 3,
        name: 'Bob Farmer',
        contactNumber: '9876543212',
        email: 'bob.farmer@example.com',
        state: 'Punjab',
        district: 'Amritsar',
        region: 'Northern',
        kycStatus: 'PENDING',
        accessStatus: 'ACTIVE',
        assignmentStatus: 'UNASSIGNED',
        assignedEmployee: null,
        assignedDate: null
      }
    ];

    const mockEmployees = [
      {
        id: 1,
        name: 'John Employee',
        phone: '9876543201',
        email: 'john.employee@company.com',
        designation: 'KYC Officer',
        state: 'Maharashtra',
        district: 'Pune',
        region: 'Western',
        status: 'ACTIVE',
        assignedFarmersCount: 15,
        kycStats: {
          approved: 8,
          pending: 5,
          referBack: 2,
          rejected: 0
        }
      },
      {
        id: 2,
        name: 'Jane Employee',
        phone: '9876543202',
        email: 'jane.employee@company.com',
        designation: 'KYC Officer',
        state: 'Gujarat',
        district: 'Ahmedabad',
        region: 'Western',
        status: 'ACTIVE',
        assignedFarmersCount: 12,
        kycStats: {
          approved: 6,
          pending: 4,
          referBack: 1,
          rejected: 1
        }
      },
      {
        id: 3,
        name: 'Mike Johnson',
        phone: '9876543203',
        email: 'mike.johnson@company.com',
        designation: 'KYC Officer',
        state: 'Punjab',
        district: 'Amritsar',
        region: 'Northern',
        status: 'ACTIVE',
        assignedFarmersCount: 8,
        kycStats: {
          approved: 5,
          pending: 2,
          referBack: 1,
          rejected: 0
        }
      },
      {
        id: 4,
        name: 'Sarah Wilson',
        phone: '9876543204',
        email: 'sarah.wilson@company.com',
        designation: 'KYC Officer',
        state: 'Karnataka',
        district: 'Bangalore',
        region: 'Southern',
        status: 'ACTIVE',
        assignedFarmersCount: 0,
        kycStats: {
          approved: 0,
          pending: 0,
          referBack: 0,
          rejected: 0
        }
      }
    ];

    setFarmers(mockFarmers);
    setEmployees(mockEmployees);
  };

  const loadMockRegistrationData = () => {
    const mockRegistrations = [
      {
        id: 1,
        name: 'John Doe',
        email: 'john.doe@example.com',
        phone: '9876543210',
        role: 'FARMER',
        status: 'PENDING',
        registrationDate: '2024-01-15',
        documents: ['Aadhar Card', 'PAN Card'],
        kycStatus: 'NOT_STARTED'
      },
      {
        id: 2,
        name: 'Jane Smith',
