import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import '../styles/ChangeUserIdPage.css';

const ChangeUserIdPage = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    currentUserId: '',
    newUserId: '',
    confirmUserId: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    // Validation
    if (formData.newUserId !== formData.confirmUserId) {
      setError('New User IDs do not match');
      setLoading(false);
      return;
    }

    if (formData.newUserId.length < 3) {
      setError('New User ID must be at least 3 characters long');
      setLoading(false);
      return;
    }

    if (formData.newUserId === formData.currentUserId) {
      setError('New User ID must be different from current User ID');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters long');
      setLoading(false);
      return;
    }

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setSuccess('User ID changed successfully!');
      setTimeout(() => {
        navigate(-1); // Go back to previous page
      }, 2000);
    } catch (err) {
      setError('Failed to change User ID. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleBack = () => {
    navigate(-1);
  };

  return (
    <div className="change-userid-page">
      <div className="change-userid-container">
        <div className="change-userid-header">
          <button className="back-button" onClick={handleBack}>
            <i className="fas fa-arrow-left"></i> Back
          </button>
          <h1>Change User ID</h1>
          <p>Update your account User ID</p>
        </div>

        <form onSubmit={handleSubmit} className="change-userid-form">
          <div className="form-group">
            <label htmlFor="currentUserId">Current User ID</label>
            <input
              type="text"
              id="currentUserId"
              name="currentUserId"
              value={formData.currentUserId}
              onChange={handleChange}
              required
              placeholder="Enter your current User ID"
            />
          </div>

          <div className="form-group">
            <label htmlFor="newUserId">New User ID</label>
            <input
              type="text"
              id="newUserId"
              name="newUserId"
              value={formData.newUserId}
              onChange={handleChange}
              required
              placeholder="Enter your new User ID"
            />
            <small style={{ color: '#666', fontSize: '0.85rem', marginTop: '5px' }}>
              Must be at least 3 characters long
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="confirmUserId">Confirm New User ID</label>
            <input
              type="text"
              id="confirmUserId"
              name="confirmUserId"
              value={formData.confirmUserId}
              onChange={handleChange}
              required
              placeholder="Confirm your new User ID"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Enter your password to confirm changes"
            />
          </div>

          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}

          <div className="form-actions">
            <button
              type="button"
              className="btn-secondary"
              onClick={handleBack}
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary"
              disabled={loading}
            >
              {loading ? 'Changing User ID...' : 'Change User ID'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ChangeUserIdPage;
