          />
          <StatsCard
            title="Pending"
            value={stats.pending}
            change=""
            changeType="warning"
            icon="â³"
          />
          <StatsCard
            title="Refer Back"
            value={stats.referBack}
            change=""
            changeType="warning"
            icon="ðŸ“"
          />
        </div>

        {/* KYC Progress Chart */}
        <div className="kyc-progress-section">
          <h3>KYC Progress Summary</h3>
          <div className="kyc-progress-grid">
            <div className="progress-card approved">
              <div className="progress-circle">
