import React from 'react';

const DataTable = ({ data, columns, onEdit, onDelete, onView, showDelete = false, customActions = [] }) => {
  const getStatusClass = (status) => {
    switch (status?.toLowerCase()) {
      case 'approved':
        return 'status-approved';
      case 'pending':
        return 'status-pending';
      case 'refer_back':
        return 'status-refer-back';
      case 'rejected':
        return 'status-rejected';
      case 'assigned':
        return 'status-assigned';
      case 'unassigned':
        return 'status-unassigned';
      default:
        return 'status-default';
    }
  };

  return (
    <div className="data-table-container">
      <table className="data-table">
        <thead>
          <tr>
            {columns.map((column, index) => (
              <th key={index}>{column.label}</th>
            ))}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {columns.map((column, colIndex) => (
                                 <td key={colIndex}>
                   {(() => {
                     const value = row[column.key];
                     
                     // Handle status fields with special styling
                     if (column.key === 'kycStatus' || column.key === 'assignmentStatus' || column.key === 'status' || column.key === 'accessStatus') {
                       return (
                         <span className={getStatusClass(value)}>
                           {value || 'N/A'}
                         </span>
                       );
                     }
                     
                     // Handle date fields
                     if (column.key === 'registrationDate' || column.key === 'assignedDate') {
                       return value ? new Date(value).toLocaleDateString() : 'N/A';
                     }
                     
                     // Handle name fields (combine firstName and lastName if needed)
                     if (column.key === 'name') {
                       if (row.firstName && row.lastName) {
                         return `${row.firstName} ${row.lastName}`;
                       } else if (row.name) {
                         return row.name;
                       } else {
                         return 'N/A';
                       }
                     }
                     
                     // Handle phone fields
                     if (column.key === 'phone' || column.key === 'phoneNumber') {
                       return row.phoneNumber || row.phone || row.contactNumber || 'N/A';
                     }
                     
                     // Handle email fields
                     if (column.key === 'email') {
                       return row.email || 'N/A';
                     }
                     
                     // Handle role fields
                     if (column.key === 'role') {
                       return row.role || 'N/A';
                     }
                     
                     // For all other fields, return the value or 'N/A'
                     return value || 'N/A';
                   })()}
                 </td>
              ))}
              <td className="actions-cell">
                <div className="action-buttons">
                  {onView && (
                    <button 
                      className="action-btn-small info"
                      onClick={() => onView(row)}
                    >
                      üëÅÔ∏è View
                    </button>
                  )}
                  {onEdit && (
                    <button 
                      className="action-btn-small primary"
                      onClick={() => onEdit(row)}
                    >
                      ‚úèÔ∏è Edit
                    </button>
                  )}
                  {customActions.map((action, index) => {
                    // Check if action should be shown based on condition
                    if (action.showCondition && !action.showCondition(row)) {
                      return null;
                    }
                    return (
                      <button
                        key={index}
                        className={`action-btn-small ${action.className || 'secondary'}`}
                        onClick={() => action.onClick(row)}
                      >
                        {action.icon} {action.label}
                      </button>
                    );
                  })}
                  {showDelete && onDelete && (
                    <button 
                      className="action-btn-small danger"
                      onClick={() => onDelete(row)}
                    >
                      üóëÔ∏è Delete
                    </button>
                  )}
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {data.length === 0 && (
        <div className="no-data">
          <p>No data available</p>
        </div>
      )}
    </div>
  );
};

export default DataTable;
