import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { authAPI } from '../api/apiService';
import { useNavigate } from 'react-router-dom';
import "../styles/Login.css";
import logo from "../assets/rightlogo.png";

// Enhanced validation schema
const schema = Yup.object().shape({
  newUserId: Yup.string()
    .required('New User ID is required')
    .min(6, 'User ID must be at least 6 characters long')
    .max(20, 'User ID must not exceed 20 characters')
    .matches(/^[a-zA-Z0-9_-]+$/, 'User ID can only contain letters, numbers, hyphens, and underscores'),
  confirmUserId: Yup.string()
    .required('Please confirm your User ID')
    .oneOf([Yup.ref('newUserId')], 'User IDs must match')
});

const ChangeUserId = () => {
  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    watch,
    reset
  } = useForm({ 
    resolver: yupResolver(schema),
    mode: 'onChange'
  });

  const [isLoading, setIsLoading] = useState(false);
  const [submitError, setSubmitError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  
  const navigate = useNavigate();
  const newUserId = watch('newUserId');

  const handleChangeUserId = async (data) => {
    setIsLoading(true);
    setSubmitError('');
    setSuccessMessage('');

    try {
      await authAPI.changeUserId({ newUserName: data.newUserId, password: '' });
      
      setSuccessMessage(`User ID changed successfully to: ${data.newUserId}`);
      reset();
      
      // Redirect after a short delay to show success message
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    } catch (error) {
      if (error.response?.data?.message) {
        setSubmitError(error.response.data.message);
      } else {
        setSubmitError('Failed to change User ID. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleBackToLogin = () => {
    navigate('/login');
  };

  return (
    <div className="login-page-container">
      <div className="login-page-main-content">
        {/* Left Section - Information Panel */}
        <div className="login-page-info-panel">
          <div className="login-page-brand-header">
            <div className="login-page-brand-logo">
              <span className="login-page-brand-icon">🌱</span>
            </div>
            <div className="login-page-brand-text">
              <h1>Date <span className="login-page-brand-accent">Agri</span> Stack</h1>
              <h2>India Farmer Registry</h2>
            </div>
          </div>

          <div className="login-page-main-title">
            <h1>Digital Agristack Transaction Enterprises</h1>
            <p>Empowering Agricultural Excellence</p>
          </div>

          <div className="login-page-platform-info">
            <h2>User ID Management</h2>
            <p>Update your User ID to keep your account secure and personalized</p>
          </div>

          <div className="login-page-tagline">
            <p>Secure • Simple • User-Friendly</p>
          </div>

          {/* Feature Highlights */}
          <div className="login-page-features-grid">
            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">🔐</div>
              <div className="login-page-feature-content">
                <h3>Secure Update</h3>
                <p>Change your User ID with enhanced security measures</p>
              </div>
            </div>

            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">⚡</div>
              <div className="login-page-feature-content">
                <h3>Quick Process</h3>
                <p>Update your User ID in just a few simple steps</p>
              </div>
            </div>

            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">🛡️</div>
              <div className="login-page-feature-content">
                <h3>Account Protection</h3>
                <p>Your account remains secure during the update process</p>
              </div>
            </div>

            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">📱</div>
              <div className="login-page-feature-content">
                <h3>Multi-Device Access</h3>
                <p>Use your new User ID from any device</p>
              </div>
            </div>

            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">✅</div>
              <div className="login-page-feature-content">
                <h3>Instant Access</h3>
                <p>Access your account immediately after the update</p>
              </div>
            </div>
          </div>
        </div>

        {/* Right Section - Change User ID Form */}
        <div className="login-page-form-section">
          <div className="login-page-card">
            {/* DATE Logo at Top */}
            <div className="login-page-date-logo-section">
              <img src={logo} alt="DATE Logo" className="login-page-date-logo" />
              <div className="login-page-date-text">
                <h3>Digital Agristack Transaction Enterprises</h3>
                <p>Empowering Agricultural Excellence</p>
              </div>
            </div>

            {/* Form Title Section */}
            <div className="login-page-login-type-section">
              <h3>Change User ID</h3>
              <p>Set a strong User ID to prevent unauthorized access to your account</p>
            </div>

            {/* Success Message */}
            {successMessage && (
              <div className="success-message">
                <span className="success-icon">✅</span>
                {successMessage}
              </div>
            )}

            {/* Change User ID Form */}
            <form onSubmit={handleSubmit(handleChangeUserId)} className="login-page-form">
              <div className="login-page-form-field">
                <label>
                  New User ID <span className="required">*</span>
                </label>
                <input
                  {...register('newUserId')}
                  type="text"
                  placeholder="Enter new User ID (6-20 characters)"
                  className={`login-page-form-input ${errors.newUserId ? 'error' : ''}`}
                  disabled={isLoading}
                />
                {errors.newUserId && (
                  <div className="error-message">
                    <span className="error-icon">⚠️</span>
                    {errors.newUserId.message}
                  </div>
                )}
                {newUserId && !errors.newUserId && (
                  <div className="input-hint">
                    <span className="hint-icon">💡</span>
                    Good! Your User ID meets the requirements.
                  </div>
                )}
              </div>

              <div className="login-page-form-field">
                <label>
                  Confirm User ID <span className="required">*</span>
                </label>
                <input
                  {...register('confirmUserId')}
                  type="text"
                  placeholder="Confirm new User ID"
                  className={`login-page-form-input ${errors.confirmUserId ? 'error' : ''}`}
                  disabled={isLoading}
                />
                {errors.confirmUserId && (
                  <div className="error-message">
                    <span className="error-icon">⚠️</span>
                    {errors.confirmUserId.message}
                  </div>
                )}
              </div>

              {submitError && (
                <div className="error-message submit-error">
                  <span className="error-icon">❌</span>
                  {submitError}
                </div>
              )}

              <div className="login-page-actions-row">
                <button 
                  type="submit" 
                  className="login-page-login-btn"
                  disabled={isLoading || isSubmitting}
                >
                  {isLoading ? (
                    <>
                      <span className="loading-spinner"></span>
                      Updating User ID...
                    </>
                  ) : (
                    'Change User ID'
                  )}
                </button>
              </div>
            </form>

            {/* Action Links */}
            <div className="login-page-action-links">
              <div className="login-link">
                <p>Want to go back? <a href="/login" onClick={handleBackToLogin}>Sign In</a></p>
              </div>
              <div className="back-link">
                <a href="/login" onClick={handleBackToLogin}>
                  ← Back to Login
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChangeUserId;
