import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";
import { authAPI } from "../api/apiService";
import { useNavigate } from 'react-router-dom';
import "../styles/ModernForms.css";
import logo from "../assets/rightlogo.png";

// âœ… Schema validation
const schema = Yup.object().shape({
  userInput: Yup.string()
    .required("Email / Phone / ID is required")
    .test(
      "valid-userInput",
      "Enter a valid Email (with '@' and '.'), 10-digit Phone number, or ID (min 6 characters)",
      function (value) {
        if (!value) return false;
 
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const phoneRegex = /^[0-9]{10}$/;
 
        const isEmail = emailRegex.test(value);
        const isPhone = phoneRegex.test(value);
        const isId = !isEmail && !isPhone && value.length >= 6;
 
        return isEmail || isPhone || isId;
      }
    ),
});
 
const ForgotPassword = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });
 
  const [showPopup, setShowPopup] = useState(false);
  const [target, setTarget] = useState("");
 
   const navigate = useNavigate();
   const onSubmit = async (data) => {
    try {
      await authAPI.forgotPassword(data.userInput);
 
      setTarget(data.userInput);
      setShowPopup(true); // Show popup on success
    } catch (error) {
      console.error("Error sending reset request:", error);
      alert("Failed to send reset link. Please try again.");
    }
  };
 
     const handlePopupClose = () => {
  setShowPopup(false);
  navigate('/otp-verification', { state: { target, type: 'password' } });
};
 
  return (
    <div className="modern-form-container">
      <div className="modern-form-card">
        {/* Form Header */}
        <div className="modern-form-header">
          <div className="modern-form-date-logo-section">
            <div className="modern-form-date-logo">
              <img src={logo} alt="DATE Logo" />
            </div>
            <div className="modern-form-date-text">
              <h3>Digital Agristack Transaction Enterprises</h3>
              <p>Empowering Agricultural Excellence</p>
            </div>
          </div>

          <div className="modern-form-title-section">
            <h2 className="modern-form-title">Forgot Password</h2>
            <p className="modern-form-subtitle">Enter your email address, click "Reset password", and we'll send you a link to reset your password.</p>
          </div>
        </div>

        {/* Forgot Password Form */}
        <form onSubmit={handleSubmit(onSubmit)} className="modern-form">
          <div className="modern-form-group">
            <label className="modern-form-label">
              Email <span className="modern-form-required">*</span>
            </label>
            <input
              {...register("userInput")}
              placeholder="Enter your Email"
              className={`modern-form-input ${errors.userInput ? 'error' : ''}`}
            />
            {errors.userInput && <div className="modern-form-error-message">{errors.userInput.message}</div>}
          </div>
          
          <div className="modern-form-actions">
            <button type="submit" className="modern-form-btn modern-form-btn-primary">
              Reset Password
            </button>
          </div>
        </form>

        {/* Success Popup */}
        {showPopup && (
          <div className="modern-form-popup">
            <div className="modern-form-popup-content">
              <h3>Success!</h3>
              <h4>
                A reset link has been sent to <strong>{target}</strong>
              </h4>
              <button onClick={handlePopupClose} className="modern-form-btn modern-form-btn-primary">OK</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
 
export default ForgotPassword;
