import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import '../styles/Login.css';
import logo from '../assets/rightlogo.png';

const ChangePassword = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    // Validation
    if (formData.newPassword !== formData.confirmPassword) {
      setError('New passwords do not match');
      setLoading(false);
      return;
    }

    if (formData.newPassword.length < 6) {
      setError('New password must be at least 6 characters long');
      setLoading(false);
      return;
    }

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      setSuccess('Password changed successfully!');
      setTimeout(() => {
        navigate(-1); // Go back to previous page
      }, 2000);
    } catch (err) {
      setError('Failed to change password. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleBack = () => {
    navigate(-1);
  };

  return (
    <div className="login-page-container forgot-password-page">
      <div className="login-page-main-content">
        {/* Left Information Panel */}
        <div className="login-page-info-panel">
          <div className="login-page-brand-header">
            <div className="login-page-brand-logo">
              <span className="login-page-brand-icon">üå±</span>
            </div>
            <div className="login-page-brand-text">
              <h1>Date <span className="login-page-brand-accent">Agri</span> Stack</h1>
              <h2>India Farmer Registry</h2>
            </div>
          </div>

          <div className="login-page-main-title">
            <h1>Digital Agristack Transaction Enterprises</h1>
            <p>Empowering Agricultural Excellence</p>
          </div>

          <div className="login-page-platform-info">
            <h2>Password Security</h2>
            <p>Keep your account safe with a strong password</p>
          </div>

          <div className="login-page-tagline">
            <p>Secure ‚Ä¢ Strong ‚Ä¢ Protected</p>
          </div>

          <div className="login-page-features-grid">
            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">üîê</div>
              <div className="login-page-feature-content">
                <h3>Strong Password</h3>
                <p>Create a secure password with our guidelines</p>
              </div>
            </div>

            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">üõ°Ô∏è</div>
              <div className="login-page-feature-content">
                <h3>Account Protection</h3>
                <p>Enhanced security for your personal data</p>
              </div>
            </div>

            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">‚ö°</div>
              <div className="login-page-feature-content">
                <h3>Quick Update</h3>
                <p>Change your password in just a few steps</p>
              </div>
            </div>

            <div className="login-page-feature-card">
              <div className="login-page-feature-icon">üîí</div>
              <div className="login-page-feature-content">
                <h3>Secure Process</h3>
                <p>Your password is encrypted and protected</p>
              </div>
            </div>
          </div>
        </div>

        {/* Right Form Section */}
        <div className="login-page-form-section">
          <div className="login-page-card">
            <div className="login-page-date-logo-section">
              <div className="login-page-date-logo">
                <img src={logo} alt="DATE Logo" />
              </div>
              <div className="login-page-date-text">
                <h3>Digital Agristack Transaction Enterprises</h3>
                <p>Empowering Agricultural Excellence</p>
              </div>
            </div>

            <div className="login-page-login-type-section">
              <h3>Change Password</h3>
              <p>Update your account password to keep it secure</p>
            </div>

            {/* Change Password Form */}
            <form onSubmit={handleSubmit} className="login-page-form">
              <div className="login-page-form-field">
                <label>
                  Current Password <span className="required">*</span>
                </label>
                <input
                  type="password"
                  name="currentPassword"
                  value={formData.currentPassword}
                  onChange={handleChange}
                  placeholder="Enter your current password"
                  className="login-page-form-input"
                  required
                />
              </div>

              <div className="login-page-form-field">
                <label>
                  New Password <span className="required">*</span>
                </label>
                <input
                  type="password"
                  name="newPassword"
                  value={formData.newPassword}
                  onChange={handleChange}
                  placeholder="Enter your new password"
                  className="login-page-form-input"
                  required
                />
              </div>

              <div className="login-page-form-field">
                <label>
                  Confirm New Password <span className="required">*</span>
                </label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  placeholder="Confirm your new password"
                  className="login-page-form-input"
                  required
                />
              </div>

              {error && <div className="error-message">{error}</div>}
              {success && <div className="verification-success">{success}</div>}

              <div className="login-page-actions-row">
                <button type="button" className="login-page-login-btn" onClick={handleBack} style={{ marginRight: '10px', background: '#6b7280' }}>
                  Back
                </button>
                <button type="submit" className="login-page-login-btn" disabled={loading}>
                  {loading ? 'Changing Password...' : 'Change Password'}
                </button>
              </div>
            </form>

            {/* Additional Help Text */}
            <div style={{ 
              textAlign: 'center', 
              marginTop: '25px', 
              padding: '20px', 
              background: 'linear-gradient(135deg, #f8fafc, #f1f5f9)', 
              borderRadius: '12px', 
              border: '1px solid #e2e8f0',
              boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)'
            }}>
              <p style={{ 
                margin: '0', 
                fontSize: '0.95rem', 
                color: '#475569',
                lineHeight: '1.5'
              }}>
                <strong style={{ color: '#15803d' }}>Password Requirements:</strong> At least 6 characters long
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChangePassword;
