import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import DataTable from '../components/DataTable';
import UserProfileDropdown from '../components/UserProfileDropdown';

const EmployeeDashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [assignedFarmers, setAssignedFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    status: '',
    state: '',
    district: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const response = await axios.get('/api/employee/assigned-farmers', { 
        headers: { Authorization: `Bearer ${token}` } 
      });

      setAssignedFarmers(response.data || []);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const getStats = () => {
    const totalAssigned = assignedFarmers.length;
    const pendingKYC = assignedFarmers.filter(farmer => farmer.kycStatus === 'Pending').length;
    const completedKYC = assignedFarmers.filter(farmer => farmer.kycStatus === 'Approved').length;

    return {
      assigned: { count: totalAssigned, change: '+5.2%' },
      pending: { count: pendingKYC, change: '-2.1%' },
      completed: { count: completedKYC, change: '+8.7%' }
    };
  };

  const getFilteredFarmers = () => {
    return assignedFarmers.filter(farmer => {
      const matchesStatus = !filters.status || farmer.kycStatus?.toLowerCase() === filters.status.toLowerCase();
      const matchesState = !filters.state || farmer.state?.toLowerCase() === filters.state.toLowerCase();
      const matchesDistrict = !filters.district || farmer.district?.toLowerCase() === filters.district.toLowerCase();

      return matchesStatus && matchesState && matchesDistrict;
    });
  };

  const handleUpdateKYC = (farmerId) => {
    console.log('Updating KYC for farmer:', farmerId);
    // Implement KYC update logic
  };

  const stats = getStats();

  const renderOverview = () => (
    <div className="overview-section">
      {/* Welcome Section */}
      <div className="welcome-section">
        <h1 className="welcome-title">Employee Dashboard</h1>
        <p className="welcome-subtitle">Welcome back! Here's what's happening with your assigned farmers.</p>
      </div>

      {/* Time Filters */}
      <div className="time-filters">
        <button className="time-filter-btn">
          <i className="fas fa-sync-alt"></i>
          Refresh
        </button>
        <button className="time-filter-btn active">
          <i className="fas fa-calendar-day"></i>
          Today
        </button>
        <button className="time-filter-btn">
          <i className="fas fa-calendar-week"></i>
          This Month
        </button>
        <button className="time-filter-btn">
          <i className="fas fa-calendar"></i>
          This Year
        </button>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stats-card">
          <div className="stats-icon farmers">
            <i className="fas fa-users"></i>
          </div>
          <div className="stats-title">Assigned Farmers</div>
          <div className="stats-value">{stats.assigned.count}</div>
          <div className={`stats-change ${stats.assigned.change.startsWith('+') ? 'positive' : 'negative'}`}>
            <i className={`fas fa-${stats.assigned.change.startsWith('+') ? 'arrow-up' : 'arrow-down'}`}></i>
            {stats.assigned.change}
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-icon employees">
            <i className="fas fa-clock"></i>
          </div>
          <div className="stats-title">Pending KYC</div>
          <div className="stats-value">{stats.pending.count}</div>
          <div className={`stats-change ${stats.pending.change.startsWith('+') ? 'positive' : 'negative'}`}>
            <i className={`fas fa-${stats.pending.change.startsWith('+') ? 'arrow-up' : 'arrow-down'}`}></i>
            {stats.pending.change}
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-icon fpo">
            <i className="fas fa-check-circle"></i>
          </div>
          <div className="stats-title">Completed KYC</div>
          <div className="stats-value">{stats.completed.count}</div>
          <div className={`stats-change ${stats.completed.change.startsWith('+') ? 'positive' : 'negative'}`}>
            <i className={`fas fa-${stats.completed.change.startsWith('+') ? 'arrow-up' : 'arrow-down'}`}></i>
            {stats.completed.change}
          </div>
        </div>
      </div>

      {/* Bottom Sections */}
      <div className="bottom-sections">
        {/* Recent Activities */}
        <div className="section-card">
          <div className="section-header">
            <h3 className="section-title">Recent Activities</h3>
            <a href="#" className="section-link">View All</a>
          </div>
          <div className="activities-list">
            <div className="activity-item">
              <div className="activity-icon success">
                <i className="fas fa-user-edit"></i>
              </div>
              <div className="activity-content">
                <div className="activity-text">KYC updated for John Doe</div>
                <div className="activity-time">20m ago</div>
              </div>
              <span className="activity-badge success">SUCCESS</span>
            </div>
            <div className="activity-item">
              <div className="activity-icon success">
                <i className="fas fa-user-check"></i>
              </div>
              <div className="activity-content">
                <div className="activity-text">KYC approved for Jane Smith</div>
                <div className="activity-time">10m ago</div>
              </div>
              <span className="activity-badge success">SUCCESS</span>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="section-card">
          <div className="section-header">
            <h3 className="section-title">Quick Actions</h3>
          </div>
          <div className="quick-actions-grid">
            <button className="quick-action-btn primary">
              <i className="fas fa-user-edit"></i>
              Update KYC
            </button>
            <button className="quick-action-btn secondary">
              <i className="fas fa-users"></i>
              View Farmers
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  const renderAssignedFarmers = () => (
    <div className="section-card">
      <div className="section-header">
        <h3 className="section-title">Assigned Farmers</h3>
      </div>

      {/* Filters Section */}
      <div className="filters-section">
        <div className="filter-group">
          <label className="filter-label">KYC Status</label>
          <select 
            className="filter-select"
            value={filters.status}
            onChange={(e) => setFilters({...filters, status: e.target.value})}
          >
            <option value="">All Status</option>
            <option value="approved">Approved</option>
            <option value="pending">Pending</option>
            <option value="rejected">Rejected</option>
          </select>
        </div>

        <div className="filter-group">
          <label className="filter-label">State</label>
          <select 
            className="filter-select"
            value={filters.state}
            onChange={(e) => setFilters({...filters, state: e.target.value})}
          >
            <option value="">All States</option>
            <option value="kerala">Kerala</option>
            <option value="tamil nadu">Tamil Nadu</option>
            <option value="karnataka">Karnataka</option>
          </select>
        </div>

        <div className="filter-group">
          <label className="filter-label">District</label>
          <select 
            className="filter-select"
            value={filters.district}
            onChange={(e) => setFilters({...filters, district: e.target.value})}
          >
            <option value="">All Districts</option>
            <option value="ernakulam">Ernakulam</option>
            <option value="thrissur">Thrissur</option>
            <option value="palakkad">Palakkad</option>
          </select>
        </div>

        <div className="filter-actions">
          <button 
            className="filter-btn-clear"
            onClick={() => setFilters({status: '', state: '', district: ''})}
          >
            <i className="fas fa-times"></i>
            Clear Filters
          </button>
        </div>
      </div>

      <DataTable
        data={getFilteredFarmers()}
        columns={[
          { key: 'name', label: 'Name' },
          { key: 'phone', label: 'Phone' },
          { key: 'state', label: 'State' },
          { key: 'district', label: 'District' },
          { 
            key: 'kycStatus', 
            label: 'KYC Status',
            render: (value) => (
              <span className={`status-badge status-${value?.toLowerCase() || 'pending'}`}>
                {value || 'Pending'}
              </span>
            )
          }
        ]}
        customActions={(item) => [
          { label: 'Update KYC', onClick: () => handleUpdateKYC(item.id), className: 'primary' }
        ]}
      />
    </div>
  );

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="dashboard">
      {/* Top Bar */}
      <div className="top-bar"></div>

      {/* Header */}
      <div className="dashboard-header">
        <div className="header-left">
          <div className="logo-section">
            <h1 className="logo-title">DATE</h1>
            <p className="logo-subtitle">Digital Agristack</p>
          </div>
        </div>
        <div className="header-right">
          <div className="user-info">
            <div className="user-avatar">
              {user?.name?.charAt(0) || 'U'}
            </div>
            <span className="user-email">{user?.email || 'user@example.com'}</span>
          </div>
        </div>
      </div>

      {/* Sidebar */}
      <div className="dashboard-sidebar">
        <div className="sidebar-header">
          <h2 className="sidebar-title">Employee Dashboard</h2>
          <p className="sidebar-subtitle">Manage assigned farmers and KYC</p>
        </div>
        <nav className="sidebar-nav">
          <div 
            className={`nav-item ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            <div>
              <i className="fas fa-chart-bar"></i>
              Dashboard
            </div>
            <i className="fas fa-chevron-down dropdown-arrow"></i>
          </div>
          <div 
            className={`nav-item ${activeTab === 'assigned-farmers' ? 'active' : ''}`}
            onClick={() => setActiveTab('assigned-farmers')}
          >
            <div>
              <i className="fas fa-user-friends"></i>
              Assigned Farmers
            </div>
            <i className="fas fa-chevron-down dropdown-arrow"></i>
          </div>
        </nav>
      </div>

      {/* Main Content */}
      <div className="dashboard-main">
        {/* User Profile Dropdown - Fixed Position */}
        <div className="user-profile-dropdown">
          <UserProfileDropdown />
        </div>

        {/* Dashboard Content */}
        <div className="dashboard-content">
          {activeTab === 'overview' && renderOverview()}
          {activeTab === 'assigned-farmers' && renderAssignedFarmers()}
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
