import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Dashboard.css';
import FarmerForm from '../components/FarmerForm';
import EmployeeForm from '../components/EmployeeForm';
import AssignmentModal from '../components/AssignmentModal';
import DeleteModal from '../components/DeleteModal';
import KYCDocumentUpload from '../components/KYCDocumentUpload';
import ViewFarmerRegistrationDetails from '../components/ViewFarmerRegistrationDetails';
import ViewEditEmployeeDetails from '../components/ViewEditEmployeeDetails';
import RegistrationApprovalModal from '../components/RegistrationApprovalModal';

import StatsCard from '../components/StatsCard';
import DataTable from '../components/DataTable';
import { superAdminAPI, farmersAPI, employeesAPI } from '../api/apiService';

const SuperAdminDashboard = () => {
  const { user, logout } = useAuth();
  const [currentView, setCurrentView] = useState('overview');
  const [farmers, setFarmers] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [registrations, setRegistrations] = useState([]);
  const [deletedRecords, setDeletedRecords] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [showAssignmentModal, setShowAssignmentModal] = useState(false);
  const [showFarmerForm, setShowFarmerForm] = useState(false);
  const [showEmployeeForm, setShowEmployeeForm] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const [showFarmerDetails, setShowFarmerDetails] = useState(false);
  const [selectedFarmerData, setSelectedFarmerData] = useState(null);
  const [showEmployeeDetails, setShowEmployeeDetails] = useState(false);
  const [selectedEmployeeData, setSelectedEmployeeData] = useState(null);
  const [showKYCDocumentUpload, setShowKYCDocumentUpload] = useState(false);
  const [selectedFarmerForKYC, setSelectedFarmerForKYC] = useState(null);
  const [showRegistrationModal, setShowRegistrationModal] = useState(false);
  const [selectedRegistration, setSelectedRegistration] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    state: '',
    district: '',
    region: '',
    kycStatus: '',
    assignmentStatus: '',
    registrationStatus: '',
    registrationRole: ''
  });

  // Load data from backend
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      // Fetch all data in parallel
      const [farmersData, employeesData, registrationsData] = await Promise.all([
        farmersAPI.getAllFarmers(),
        employeesAPI.getAllEmployees(),
        superAdminAPI.getAllUsers()
      ]);

      console.log('Fetched data:', { farmersData, employeesData, registrationsData });
      setFarmers(farmersData);
      setEmployees(employeesData);
      setRegistrations(registrationsData);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getFilteredFarmers = () => {
    return farmers.filter(farmer => {
      const matchesState = !filters.state || farmer.state === filters.state;
      const matchesDistrict = !filters.district || farmer.district === filters.district;
      const matchesRegion = !filters.region || farmer.region === filters.region;
      const matchesKycStatus = !filters.kycStatus || farmer.kycStatus === filters.kycStatus;
      const matchesAssignmentStatus = !filters.assignmentStatus || farmer.accessStatus === filters.assignmentStatus;
      
      return matchesState && matchesDistrict && matchesRegion && matchesKycStatus && matchesAssignmentStatus;
    });
  };

  const getFilteredEmployees = () => {
    if (!selectedEmployee) return employees;
    return employees.filter(emp => emp.id === parseInt(selectedEmployee));
  };

  const getFilteredRegistrations = () => {
    return registrations.filter(registration => {
      const matchesStatus = !filters.registrationStatus || registration.accessStatus === filters.registrationStatus;
      const matchesRole = !filters.registrationRole || registration.role === filters.registrationRole;
      
      return matchesStatus && matchesRole;
    });
  };

  const getStats = () => {
    const totalFarmers = farmers.length;
    const unassignedFarmers = farmers.filter(f => f.accessStatus === 'PENDING').length;
    const pendingKyc = farmers.filter(f => f.kycStatus === 'PENDING').length;
    const overdueKyc = farmers.filter(f => {
      if (f.assignedDate) {
        const assignedDate = new Date(f.assignedDate);
        const daysDiff = (new Date() - assignedDate) / (1000 * 60 * 60 * 24);
        return daysDiff > 7 && f.kycStatus === 'PENDING';
      }
      return false;
    }).length;
    const pendingRegistrations = registrations.filter(r => r.accessStatus === 'PENDING').length;
    const totalEmployees = employees.length;

    return {
      totalFarmers,
      unassignedFarmers,
      pendingKyc,
      overdueKyc,
      pendingRegistrations,
      totalEmployees
    };
  };

  const handleDelete = (item, type) => {
    setItemToDelete({ item, type });
    setShowDeleteModal(true);
  };

  const confirmDelete = async () => {
    if (!itemToDelete) return;

    const { item, type } = itemToDelete;
    try {
      if (type === 'farmer') {
        await farmersAPI.deleteFarmer(item.id);
        setFarmers(prev => prev.filter(f => f.id !== item.id));
      } else if (type === 'employee') {
        await employeesAPI.deleteEmployee(item.id);
        setEmployees(prev => prev.filter(e => e.id !== item.id));
      } else if (type === 'registration') {
        // Handle registration deletion if needed
        setRegistrations(prev => prev.filter(r => r.id !== item.id));
      }

      const deletedRecord = {
        id: item.id,
        entityType: type,
        entityName: item.name || item.email || item.phoneNumber,
        deletedBy: user.name,
        deletedAt: new Date().toISOString(),
        reason: itemToDelete.reason || 'No reason provided'
      };

      setDeletedRecords(prev => [...prev, deletedRecord]);
      setShowDeleteModal(false);
      setItemToDelete(null);
    } catch (error) {
      console.error('Error deleting item:', error);
      alert('Failed to delete item. Please try again.');
    }
  };

  const handleLogout = () => {
    logout();
  };

  const handleViewFarmer = (farmer) => {
    // Convert the farmer data to match the ViewFarmerRegistrationDetails component structure
    const farmerData = {
      // Personal Information
      firstName: farmer.firstName || '',
      lastName: farmer.lastName || '',
      dateOfBirth: farmer.dob || '1990-01-01',
      gender: farmer.gender || 'Male',
      mobileNumber: farmer.contactNumber || '',
      email: farmer.email || 'farmer@example.com',
      maritalStatus: 'Married', // Default value
      religion: 'Hindu', // Default value
      caste: 'General', // Default value
      category: 'General', // Default value
      education: farmer.education || 'High School',
      
      // Address Information
      village: farmer.village || '',
      postOffice: 'Sample Post Office', // Default value
      policeStation: 'Sample Police Station', // Default value
      district: farmer.district || '',
      state: farmer.state || '',
      pincode: farmer.zipcode || '',
      
      // Professional Information
      occupation: 'Farmer', // Default value
      annualIncome: '50000', // Default value
      landOwnership: 'Owned', // Default value
      landArea: '5', // Default value
      irrigationType: 'Tube Well', // Default value
      soilType: 'Alluvial', // Default value
      
      // Crop Information
      primaryCrop: 'Wheat', // Default value
      secondaryCrop: 'Rice', // Default value
      cropSeason: 'Rabi', // Default value
      farmingExperience: farmer.experience || '10',
      
      // Bank Information
      bankName: farmer.bankName || 'State Bank of India',
      branchName: farmer.branchName || 'Main Branch',
      accountNumber: farmer.accountNumber || '1234567890',
      ifscCode: farmer.ifscCode || 'SBIN0001234',
      accountType: 'Savings', // Default value
      
      // Document Information
      aadhaarNumber: '123456789012', // Default value
      panNumber: 'ABCDE1234F', // Default value
      voterId: 'ABC1234567', // Default value
      rationCardNumber: '123456789', // Default value
      
      // Status and Assignment
      status: farmer.accessStatus || 'PENDING',
      kycStatus: farmer.kycStatus || 'PENDING',
      assignedEmployee: farmer.assignedEmployee || '',
      assignedDate: farmer.assignedDate || '',
      
      // Photo
      photo: farmer.photoFileName ? `/uploads/photos/${farmer.photoFileName}` : null,
      
      // Registration date
      registrationDate: farmer.registrationDate || new Date().toISOString()
    };
    
    console.log('Farmer data for view:', farmerData);
    setSelectedFarmerData(farmerData);
    setShowFarmerDetails(true);
  };

  const handleCloseFarmerDetails = () => {
    setShowFarmerDetails(false);
    setSelectedFarmerData(null);
  };

  const handleViewEmployee = (employee) => {
    setSelectedEmployeeData(employee);
    setShowEmployeeDetails(true);
  };

  const handleCloseEmployeeDetails = () => {
    setShowEmployeeDetails(false);
    setSelectedEmployeeData(null);
  };

  const handleUpdateEmployee = async (updatedData) => {
    try {
      await employeesAPI.updateEmployee(selectedEmployeeData.id, updatedData);
      setEmployees(prev => prev.map(emp => 
        emp.id === selectedEmployeeData.id ? { ...emp, ...updatedData } : emp
      ));
      setShowEmployeeDetails(false);
      setSelectedEmployeeData(null);
    } catch (error) {
      console.error('Error updating employee:', error);
      alert('Failed to update employee. Please try again.');
    }
  };

  const handleKYCDocumentUpload = (farmer) => {
    setSelectedFarmerForKYC(farmer);
    setShowKYCDocumentUpload(true);
  };

  const handleCloseKYCDocumentUpload = () => {
    setShowKYCDocumentUpload(false);
    setSelectedFarmerForKYC(null);
  };

  const handleKYCApprove = async (farmerId, documents) => {
    try {
      // Update farmer KYC status
      setFarmers(prev => prev.map(farmer => 
        farmer.id === farmerId 
          ? { ...farmer, kycStatus: 'APPROVED' }
          : farmer
      ));
      setShowKYCDocumentUpload(false);
      setSelectedFarmerForKYC(null);
      alert('KYC approved successfully!');
    } catch (error) {
      console.error('Error approving KYC:', error);
      alert('Failed to approve KYC. Please try again.');
    }
  };

  const handleKYCReject = async (farmerId, reason, documents) => {
    try {
      setFarmers(prev => prev.map(farmer => 
        farmer.id === farmerId 
          ? { ...farmer, kycStatus: 'REJECTED' }
          : farmer
      ));
      setShowKYCDocumentUpload(false);
      setSelectedFarmerForKYC(null);
      alert('KYC rejected successfully!');
    } catch (error) {
      console.error('Error rejecting KYC:', error);
      alert('Failed to reject KYC. Please try again.');
    }
  };

  const handleKYCReferBack = async (farmerId, reason, documents) => {
    try {
      setFarmers(prev => prev.map(farmer => 
        farmer.id === farmerId 
          ? { ...farmer, kycStatus: 'REFER_BACK' }
          : farmer
      ));
      setShowKYCDocumentUpload(false);
      setSelectedFarmerForKYC(null);
      alert('KYC referred back successfully!');
    } catch (error) {
      console.error('Error referring back KYC:', error);
      alert('Failed to refer back KYC. Please try again.');
    }
  };

  const handleViewRegistration = (registration) => {
    setSelectedRegistration(registration);
    setShowRegistrationModal(true);
  };

  const handleCloseRegistrationModal = () => {
    setShowRegistrationModal(false);
    setSelectedRegistration(null);
  };

  const handleApproveRegistration = async (registrationId) => {
    try {
      await superAdminAPI.approveUser(registrationId, selectedRegistration.role);
      setRegistrations(prev => prev.map(reg => 
        reg.id === registrationId 
          ? { ...reg, accessStatus: 'APPROVED' }
          : reg
      ));
      setShowRegistrationModal(false);
      setSelectedRegistration(null);
      alert('Registration approved successfully!');
    } catch (error) {
      console.error('Error approving registration:', error);
      alert('Failed to approve registration. Please try again.');
    }
  };

  const handleRejectRegistration = async (registrationId, reason) => {
    try {
      await superAdminAPI.rejectUser(registrationId, reason);
      setRegistrations(prev => prev.map(reg => 
        reg.id === registrationId 
          ? { ...reg, accessStatus: 'REJECTED' }
          : reg
      ));
      setShowRegistrationModal(false);
      setSelectedRegistration(null);
      alert('Registration rejected successfully!');
    } catch (error) {
      console.error('Error rejecting registration:', error);
      alert('Failed to reject registration. Please try again.');
    }
  };

  const renderOverview = () => (
    <div className="dashboard-content">
      {loading ? (
        <div className="loading">Loading...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <>
          <div className="stats-grid">
            <StatsCard
              title="Total Farmers"
              value={getStats().totalFarmers}
              icon="👨‍🌾"
              color="blue"
            />
            <StatsCard
              title="Total Employees"
              value={getStats().totalEmployees}
              icon="👥"
              color="green"
            />
            <StatsCard
              title="Pending Registrations"
              value={getStats().pendingRegistrations}
              icon="📋"
              color="orange"
            />
            <StatsCard
              title="Unassigned Farmers"
              value={getStats().unassignedFarmers}
              icon="📋"
              color="yellow"
            />
            <StatsCard
              title="Pending KYC"
              value={getStats().pendingKyc}
              icon="⏳"
              color="purple"
            />
            <StatsCard
              title="Overdue KYC"
              value={getStats().overdueKyc}
              icon="⚠️"
              color="red"
            />
          </div>

          <div className="quick-actions">
            <h3>Quick Actions</h3>
            <div className="action-buttons">
              <button 
                className="action-btn primary"
                onClick={() => setShowFarmerForm(true)}
              >
                ➕ Add Farmer
              </button>
              <button 
                className="action-btn primary"
                onClick={() => setShowEmployeeForm(true)}
              >
                ➕ Add Employee
              </button>
                             <button 
                 className="action-btn secondary"
                 onClick={() => setShowAssignmentModal(true)}
               >
                 🔗 Approve Farmers
               </button>
              <button 
                className="action-btn secondary"
                onClick={() => setCurrentView('registrations')}
              >
                📋 View Registrations
              </button>
            </div>
          </div>

          <div className="todo-panel">
            <h3>To-Do List</h3>
            <div className="todo-items">
              {getStats().pendingRegistrations > 0 && (
                <div className="todo-item">
                  <span className="todo-icon">📋</span>
                  <span>{getStats().pendingRegistrations} registrations need approval</span>
                </div>
              )}
                             {getStats().unassignedFarmers > 0 && (
                 <div className="todo-item">
                   <span className="todo-icon">📋</span>
                   <span>{getStats().unassignedFarmers} farmers need approval</span>
                 </div>
               )}
              {getStats().overdueKyc > 0 && (
                <div className="todo-item">
                  <span className="todo-icon">⚠️</span>
                  <span>{getStats().overdueKyc} KYC cases overdue</span>
                </div>
              )}
              {employees.filter(emp => emp.kycSummary?.pending > 10).map(emp => (
                <div key={emp.id} className="todo-item">
                  <span className="todo-icon">📊</span>
                  <span>{emp.name} has {emp.kycSummary.pending} pending cases</span>
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );

  const renderFarmers = () => (
    <div className="dashboard-content">
      <div className="filters-section">
        <h3>Farmer Management</h3>
        <div className="filters">
          <select 
            value={filters.state} 
            onChange={(e) => setFilters(prev => ({ ...prev, state: e.target.value }))}
          >
            <option value="">All States</option>
            <option value="Maharashtra">Maharashtra</option>
            <option value="Gujarat">Gujarat</option>
            <option value="Punjab">Punjab</option>
          </select>
          <select 
            value={filters.kycStatus} 
            onChange={(e) => setFilters(prev => ({ ...prev, kycStatus: e.target.value }))}
          >
            <option value="">All KYC Status</option>
            <option value="APPROVED">Approved</option>
            <option value="PENDING">Pending</option>
            <option value="REFER_BACK">Refer Back</option>
            <option value="REJECTED">Rejected</option>
          </select>
                     <select 
             value={filters.assignmentStatus} 
             onChange={(e) => setFilters(prev => ({ ...prev, assignmentStatus: e.target.value }))}
           >
             <option value="">All Status</option>
             <option value="PENDING">Pending</option>
             <option value="APPROVED">Approved</option>
             <option value="REJECTED">Rejected</option>
           </select>
        </div>
      </div>

      {loading ? (
        <div className="loading">Loading farmers...</div>
      ) : (
                      <DataTable
          data={getFilteredFarmers()}
          columns={[
            { key: 'name', label: 'Name' },
            { key: 'contactNumber', label: 'Phone' },
            { key: 'state', label: 'State' },
            { key: 'district', label: 'District' },
            { key: 'kycStatus', label: 'KYC Status' },
            { key: 'accessStatus', label: 'Status' },
            { key: 'assignedEmployee', label: 'Assigned Employee' }
          ]}
          onView={handleViewFarmer}
          onEdit={(farmer) => {
            console.log('Edit farmer:', farmer);
            setShowFarmerForm(true);
          }}
          onDelete={(farmer) => handleDelete(farmer, 'farmer')}
          showDelete={true}
          customActions={[
            {
              icon: '📁',
              label: 'KYC Docs',
              className: 'secondary',
              onClick: handleKYCDocumentUpload
            }
          ]}
        />
      )}
    </div>
  );

  const renderEmployees = () => (
    <div className="dashboard-content">
      <div className="filters-section">
        <h3>Employee Management</h3>
        <div className="filters">
          <select 
            value={selectedEmployee} 
            onChange={(e) => setSelectedEmployee(e.target.value)}
          >
            <option value="">All Employees</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>{emp.name}</option>
            ))}
          </select>
        </div>
      </div>

      {loading ? (
        <div className="loading">Loading employees...</div>
      ) : (
        <DataTable
          data={getFilteredEmployees()}
          columns={[
            { key: 'name', label: 'Name' },
            { key: 'email', label: 'Email' },
            { key: 'contactNumber', label: 'Phone' },
            { key: 'department', label: 'Department' },
            { key: 'designation', label: 'Designation' },
            { key: 'status', label: 'Status' }
          ]}
          onView={handleViewEmployee}
          onEdit={(employee) => {
            setShowEmployeeForm(true);
            console.log('Edit employee:', employee);
          }}
          onDelete={(employee) => handleDelete(employee, 'employee')}
          showDelete={true}
        />
      )}
    </div>
  );

  const renderRegistrations = () => (
    <div className="dashboard-content">
      <div className="filters-section">
        <h3>Registration Management</h3>
        <div className="filters">
          <select 
            value={filters.registrationStatus} 
            onChange={(e) => setFilters(prev => ({ ...prev, registrationStatus: e.target.value }))}
          >
            <option value="">All Status</option>
            <option value="PENDING">Pending</option>
            <option value="APPROVED">Approved</option>
            <option value="REJECTED">Rejected</option>
          </select>
          <select 
            value={filters.registrationRole} 
            onChange={(e) => setFilters(prev => ({ ...prev, registrationRole: e.target.value }))}
          >
            <option value="">All Roles</option>
            <option value="FARMER">Farmer</option>
            <option value="EMPLOYEE">Employee</option>
            <option value="FPO">FPO</option>
          </select>
        </div>
      </div>

      {loading ? (
        <div className="loading">Loading registrations...</div>
      ) : (
        <DataTable
          data={getFilteredRegistrations()}
          columns={[
            { key: 'name', label: 'Name' },
            { key: 'email', label: 'Email' },
            { key: 'contactNumber', label: 'Phone' },
            { key: 'role', label: 'Role' },
            { key: 'accessStatus', label: 'Status' },
            { key: 'registrationDate', label: 'Registration Date' }
          ]}
          onView={handleViewRegistration}
          onEdit={(registration) => {
            console.log('Edit registration:', registration);
          }}
          onDelete={(registration) => handleDelete(registration, 'registration')}
          showDelete={true}
          customActions={[
            {
              icon: '✅',
              label: 'Approve',
              className: 'success',
              onClick: (registration) => {
                if (registration.accessStatus === 'PENDING') {
                  setSelectedRegistration(registration);
                  setShowRegistrationModal(true);
                }
              },
              showCondition: (registration) => registration.accessStatus === 'PENDING'
            },
            {
              icon: '❌',
              label: 'Reject',
              className: 'danger',
              onClick: (registration) => {
                if (registration.accessStatus === 'PENDING') {
                  const reason = prompt('Please provide rejection reason:');
                  if (reason) {
                    handleRejectRegistration(registration.id, reason);
                  }
                }
              },
              showCondition: (registration) => registration.accessStatus === 'PENDING'
            }
          ]}
        />
      )}
    </div>
  );

  const renderAuditTrail = () => (
    <div className="dashboard-content">
      <h3>Audit Trail - Deleted Records</h3>
      <div className="audit-table">
        <table>
          <thead>
            <tr>
              <th>Entity Type</th>
              <th>Entity Name</th>
              <th>Deleted By</th>
              <th>Deleted At</th>
              <th>Reason</th>
            </tr>
          </thead>
          <tbody>
            {deletedRecords.map((record, index) => (
              <tr key={index}>
                <td>{record.entityType}</td>
                <td>{record.entityName}</td>
                <td>{record.deletedBy}</td>
                <td>{new Date(record.deletedAt).toLocaleDateString()}</td>
                <td>{record.reason}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="header-left">
          <h1>Super Admin Dashboard</h1>
          <p>Welcome back, {user?.name}</p>
        </div>
        <div className="header-right">
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      <div className="dashboard-nav">
        <button 
          className={`nav-btn ${currentView === 'overview' ? 'active' : ''}`}
          onClick={() => setCurrentView('overview')}
        >
          📊 Overview
        </button>
        <button 
          className={`nav-btn ${currentView === 'farmers' ? 'active' : ''}`}
          onClick={() => setCurrentView('farmers')}
        >
          👨‍🌾 Farmers
        </button>
        <button 
          className={`nav-btn ${currentView === 'employees' ? 'active' : ''}`}
          onClick={() => setCurrentView('employees')}
        >
          👥 Employees
        </button>
        <button 
          className={`nav-btn ${currentView === 'registrations' ? 'active' : ''}`}
          onClick={() => setCurrentView('registrations')}
        >
          📋 Registrations
        </button>
        <button 
          className={`nav-btn ${currentView === 'audit' ? 'active' : ''}`}
          onClick={() => setCurrentView('audit')}
        >
          📋 Audit Trail
        </button>
      </div>

      <div className="dashboard-main">
        {currentView === 'overview' && renderOverview()}
        {currentView === 'farmers' && renderFarmers()}
        {currentView === 'employees' && renderEmployees()}
        {currentView === 'registrations' && renderRegistrations()}
        {currentView === 'audit' && renderAuditTrail()}
      </div>

      {showFarmerForm && (
        <FarmerForm 
          onClose={() => setShowFarmerForm(false)}
          onSubmit={async (farmerData) => {
            try {
              const newFarmer = await farmersAPI.createFarmer(farmerData);
              setFarmers(prev => [...prev, newFarmer]);
              setShowFarmerForm(false);
            } catch (error) {
              console.error('Error creating farmer:', error);
              alert('Failed to create farmer. Please try again.');
            }
          }}
        />
      )}

      {showEmployeeForm && (
        <EmployeeForm 
          onClose={() => setShowEmployeeForm(false)}
          onSubmit={async (employeeData) => {
            try {
              const newEmployee = await employeesAPI.createEmployee(employeeData);
              setEmployees(prev => [...prev, newEmployee]);
              setShowEmployeeForm(false);
            } catch (error) {
              console.error('Error creating employee:', error);
              alert('Failed to create employee. Please try again.');
            }
          }}
        />
      )}

      {showAssignmentModal && (
        <AssignmentModal 
          farmers={farmers.filter(f => f.accessStatus === 'PENDING')}
          employees={employees}
          onClose={() => setShowAssignmentModal(false)}
          onAssign={async (assignments) => {
            try {
              for (const assignment of assignments) {
                await farmersAPI.assignFarmer(assignment.farmerId, assignment.employeeId);
              }
              setFarmers(prev => prev.map(farmer => {
                const assignment = assignments.find(a => a.farmerId === farmer.id);
                if (assignment) {
                  return {
                    ...farmer,
                    accessStatus: 'APPROVED',
                    assignedEmployee: assignment.employeeName,
                    assignedDate: new Date().toISOString().split('T')[0]
                  };
                }
                return farmer;
              }));
              setShowAssignmentModal(false);
            } catch (error) {
              console.error('Error assigning farmers:', error);
              alert('Failed to assign farmers. Please try again.');
            }
          }}
        />
      )}

      {showDeleteModal && (
        <DeleteModal
          item={itemToDelete?.item}
          type={itemToDelete?.type}
          onClose={() => {
            setShowDeleteModal(false);
            setItemToDelete(null);
          }}
          onConfirm={confirmDelete}
        />
      )}

      {showFarmerDetails && (
        <ViewFarmerRegistrationDetails
          farmerData={selectedFarmerData}
          onClose={handleCloseFarmerDetails}
        />
      )}
      
      {showEmployeeDetails && (
        <ViewEditEmployeeDetails
          employeeData={selectedEmployeeData}
          onClose={handleCloseEmployeeDetails}
          onUpdate={handleUpdateEmployee}
        />
      )}
      
      {showKYCDocumentUpload && (
        <KYCDocumentUpload
          isOpen={showKYCDocumentUpload}
          onClose={handleCloseKYCDocumentUpload}
          farmer={selectedFarmerForKYC}
          onApprove={handleKYCApprove}
          onReject={handleKYCReject}
          onReferBack={handleKYCReferBack}
        />
      )}

      {showRegistrationModal && (
        <RegistrationApprovalModal
          isOpen={showRegistrationModal}
          onClose={handleCloseRegistrationModal}
          registration={selectedRegistration}
          onApprove={handleApproveRegistration}
          onReject={handleRejectRegistration}
        />
      )}
    </div>
  );
};

export default SuperAdminDashboard;
