import axios from 'axios';

// Create axios instance
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:8080',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Hardcoded credentials for frontend testing
const HARDCODED_CREDENTIALS = {
  'admin': { password: 'Admin@123', role: 'ADMIN', name: 'Admin User', email: 'admin@agristack.com' },
  'superadmin': { password: 'SuperAdmin@123', role: 'SUPER_ADMIN', name: 'Super Admin User', email: 'superadmin@agristack.com' },
  'employee': { password: 'Employee@123', role: 'EMPLOYEE', name: 'Employee User', email: 'employee@agristack.com' },
  'farmer': { password: 'Farmer@123', role: 'FARMER', name: 'Farmer User', email: 'farmer@agristack.com' },
  'fpo': { password: 'FPO@123', role: 'FPO', name: 'FPO User', email: 'fpo@agristack.com' },
  // Additional test credentials
  'testadmin': { password: 'Admin@123', role: 'ADMIN', name: 'Test Admin', email: 'testadmin@agristack.com' },
  'testemployee': { password: 'Employee@123', role: 'EMPLOYEE', name: 'Test Employee', email: 'testemployee@agristack.com' },
  'testfarmer': { password: 'Farmer@123', role: 'FARMER', name: 'Test Farmer', email: 'testfarmer@agristack.com' }
};

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Mock API responses for development
const mockResponses = {
  // Login endpoint
  '/auth/login': (config) => {
    const { userName, password } = JSON.parse(config.data);
    console.log('Mock API - Login attempt:', { userName, password });
    
    const userCreds = HARDCODED_CREDENTIALS[userName];
    
    if (userCreds && userCreds.password === password) {
      console.log('Mock API - Login successful for:', userName);
      return {
        status: 200,
        data: {
          token: `mock-token-${userName}-${Date.now()}`,
          role: userCreds.role,
          userName: userName,
          name: userCreds.name,
          email: userCreds.email,
          forcePasswordChange: false,
          status: 'ACTIVE'
        }
      };
    } else {
      console.log('Mock API - Login failed for:', userName);
      throw new Error('Invalid credentials');
    }
  },

  // User profile endpoint
  '/api/auth/users/profile': (config) => {
    const token = config.headers.Authorization;
    console.log('Mock API - Profile request with token:', token);
    
    // Extract username from token for demo purposes
    let userName = 'admin'; // default
    if (token && token.includes('mock-token-')) {
      const tokenParts = token.split('-');
      if (tokenParts.length >= 3) {
        userName = tokenParts[2];
      }
    }
    
    const userCreds = HARDCODED_CREDENTIALS[userName] || HARDCODED_CREDENTIALS['admin'];
    
    return {
      status: 200,
      data: {
        userName: userName,
        name: userCreds.name,
        email: userCreds.email,
        role: userCreds.role,
        forcePasswordChange: false,
        status: 'ACTIVE'
      }
    };
  },

  // Alternative profile endpoint
  '/auth/me': (config) => {
    const token = config.headers.Authorization;
    console.log('Mock API - /auth/me request with token:', token);
    
    let userName = 'admin'; // default
    if (token && token.includes('mock-token-')) {
      const tokenParts = token.split('-');
      if (tokenParts.length >= 3) {
        userName = tokenParts[2];
      }
    }
    
    const userCreds = HARDCODED_CREDENTIALS[userName] || HARDCODED_CREDENTIALS['admin'];
    
    return {
      status: 200,
      data: {
        userName: userName,
        name: userCreds.name,
        email: userCreds.email,
        role: userCreds.role,
        forcePasswordChange: false,
        status: 'ACTIVE'
      }
    };
  },

  // Forgot Password endpoint
  '/api/auth/forgot-password': (config) => {
    const { emailOrPhone } = JSON.parse(config.data);
    console.log('Mock API - Forgot password request for:', emailOrPhone);
    
    return {
      status: 200,
      data: {
        message: 'Password reset link sent successfully',
        email: emailOrPhone
      }
    };
  },

  // Forgot User ID endpoint
  '/api/auth/forgot-user-id': (config) => {
    const { emailOrPhone } = JSON.parse(config.data);
    console.log('Mock API - Forgot user ID request for:', emailOrPhone);
    
    return {
      status: 200,
      data: {
        message: 'User ID sent successfully',
        email: emailOrPhone
      }
    };
  },

  // Reset Password Confirm endpoint
  '/auth/reset-password/confirm': (config) => {
    const { emailOrPhone, newPassword, confirmPassword } = JSON.parse(config.data);
    console.log('Mock API - Reset password confirm request for:', emailOrPhone);
    
    if (newPassword !== confirmPassword) {
      throw new Error('Passwords do not match');
    }
    
    if (newPassword.length < 6) {
      throw new Error('Password must be at least 6 characters');
    }
    
    return {
      status: 200,
      data: {
        message: 'Password changed successfully',
        email: emailOrPhone
      }
    };
  },

  // Registration endpoints
  '/api/auth/register': (config) => {
    console.log('Mock API - Registration request');
    return {
      status: 200,
      data: {
        message: 'User registered successfully',
        userId: `user-${Date.now()}`
      }
    };
  },

  '/api/auth/send-otp': (config) => {
    console.log('Mock API - Send OTP request');
    return {
      status: 200,
      data: {
        message: 'OTP sent successfully',
        otp: '123456' // Hardcoded OTP for testing
      }
    };
  },

  '/api/auth/verify-otp': (config) => {
    const { otp, target } = JSON.parse(config.data);
    console.log('Mock API - Verify OTP request:', otp);
    
    if (otp === '123456') {
      return {
        status: 200,
        data: {
          message: 'OTP verified successfully',
          verified: true,
          target: target
        }
      };
    } else {
      throw new Error('Invalid OTP');
    }
  },

  // Resend OTP endpoint
  '/api/auth/resend-otp': (config) => {
    const { target } = JSON.parse(config.data);
    console.log('Mock API - Resend OTP request for:', target);
    
    return {
      status: 200,
      data: {
        message: 'OTP resent successfully',
        target: target
      }
    };
  },

  // Country and state endpoints
  '/api/auth/countries': () => {
    return {
      status: 200,
      data: [
        { id: 1, name: 'India' },
        { id: 2, name: 'United States' },
        { id: 3, name: 'United Kingdom' }
      ]
    };
  },

  '/api/auth/states': (config) => {
    const { countryId } = JSON.parse(config.data);
    console.log('Mock API - States request for country:', countryId);
    
    return {
      status: 200,
      data: [
        { id: 1, name: 'Maharashtra', countryId: 1 },
        { id: 2, name: 'Karnataka', countryId: 1 },
        { id: 3, name: 'Tamil Nadu', countryId: 1 },
        { id: 4, name: 'Kerala', countryId: 1 },
        { id: 5, name: 'Andhra Pradesh', countryId: 1 }
      ]
    };
  }
};

// Override axios methods to provide mock responses
const originalGet = api.get;
const originalPost = api.post;

api.get = async (url, config = {}) => {
  console.log('Mock API - GET request:', url);
  
  if (mockResponses[url]) {
    const response = mockResponses[url](config);
    console.log('Mock API - GET response:', response);
    return response;
  }
  
  return originalGet(url, config);
};

api.post = async (url, data, config = {}) => {
  console.log('Mock API - POST request:', url, data);
  
  if (mockResponses[url]) {
    const mockConfig = { ...config, data: JSON.stringify(data) };
    const response = mockResponses[url](mockConfig);
    console.log('Mock API - POST response:', response);
    return response;
  }
  
  return originalPost(url, data, config);
};

export default api;
