import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Login.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setLoading(true);

    try {
      // Mock API call - replace with actual API
      const response = await mockLoginAPI(formData);
      
      if (response.success) {
        login(response.user, response.token);
        
        // Redirect based on role
        switch (response.user.role) {
          case 'SUPER_ADMIN':
            navigate('/superadmin/dashboard');
            break;
          case 'ADMIN':
            navigate('/admin/dashboard');
            break;
          case 'EMPLOYEE':
            navigate('/employee/dashboard');
            break;
          default:
            navigate('/admin/dashboard');
        }
      } else {
        setErrors({ general: response.message });
      }
    } catch (error) {
      setErrors({ general: 'Login failed. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Mock API function - replace with actual API call
  const mockLoginAPI = async (credentials) => {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1000));
    
    // Mock users for testing
    const mockUsers = {
      'admin@agri.com': {
        id: 1,
        name: 'Admin User',
        email: 'admin@agri.com',
        role: 'ADMIN',
        token: 'admin-token-123'
      },
      'superadmin@agri.com': {
        id: 2,
        name: 'Super Admin',
        email: 'superadmin@agri.com',
        role: 'SUPER_ADMIN',
        token: 'superadmin-token-456'
      },
      'employee@agri.com': {
        id: 3,
        name: 'Employee User',
        email: 'employee@agri.com',
        role: 'EMPLOYEE',
        token: 'employee-token-789'
      }
    };

    const user = mockUsers[credentials.email];
    
    if (user && credentials.password === 'password123') {
      return {
        success: true,
        user: user,
        token: user.token
      };
    } else {
      return {
        success: false,
        message: 'Invalid credentials'
      };
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="logo">
            <span className="logo-icon">ðŸŒ¾</span>
            <h1>AgriStack</h1>
          </div>
          <p className="subtitle">Farmer Management System</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {errors.general && (
            <div className="error-message">
              {errors.general}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? 'error' : ''}
              placeholder="Enter your email"
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={errors.password ? 'error' : ''}
              placeholder="Enter your password"
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={loading}
          >
            {loading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        <div className="demo-credentials">
          <h4>Demo Credentials:</h4>
          <div className="credential-item">
            <strong>Admin:</strong> admin@agri.com / password123
          </div>
          <div className="credential-item">
            <strong>Super Admin:</strong> superadmin@agri.com / password123
          </div>
          <div className="credential-item">
            <strong>Employee:</strong> employee@agri.com / password123
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
