              <h4>Success Rate</h4>
              <span className="metric-value">
                {stats.totalAssigned > 0 ? Math.round(((stats.approved + stats.pending) / stats.totalAssigned) * 100) : 0}%
              </span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="dashboard">
      {/* Top Bar matching Super Admin layout */}
      <div className="top-bar">
        <div className="top-bar-left">
          <div className="logo-container">
            <div className="logo-text">
              <h1 className="logo-title">DATE</h1>
              <p className="logo-subtitle">Digital Agristack Transaction Enterprises</p>
            </div>
          </div>
          <div className="date-display">{new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</div>
        </div>
        <div className="top-bar-right">
          <ThemeDropdown />
          <UserProfileDropdown 
            onLogout={handleLogout}
            onShowChangePassword={() => setShowChangePassword(true)}
            onShowChangeUserId={() => setShowChangeUserId(true)}
          />
        </div>
      </div>
      {/* Sidebar */}
      <aside className="dashboard-sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <div className="logo-text">Welcome !!!</div>
            <div className="logo-subtitle">Employee</div>
          </div>
        </div>
        <nav className="sidebar-nav">
          <button className={`nav-item ${activeTab === 'overview' ? 'active' : ''}`} onClick={() => setActiveTab('overview')}>ðŸ“Š Overview</button>
          <button className={`nav-item ${activeTab === 'farmers' ? 'active' : ''}`} onClick={() => setActiveTab('farmers')}>ðŸ‘¨â€ðŸŒ¾ Assigned Farmers</button>
          <button className={`nav-item ${activeTab === 'progress' ? 'active' : ''}`} onClick={() => setActiveTab('progress')}>ðŸ“ˆ KYC Progress</button>
          <button className={`nav-item ${activeTab === 'todo' ? 'active' : ''}`} onClick={() => setActiveTab('todo')}>ðŸ“‹ To-Do List</button>
          <button className={`nav-item ${activeTab === 'kyc-summary' ? 'active' : ''}`} onClick={() => setActiveTab('kyc-summary')}>ðŸ“ KYC Summary</button>
          <button className="nav-item" onClick={() => navigate('/analytical-dashboard')}>ðŸ“ˆ Dashboard</button>
        </nav>
      </aside>



      {/* Main Content using shared frame */}
      <div className="dashboard-main">
        <div className="dashboard-content">
          {activeTab === 'overview' && !(
            showFarmerForm ||
            showKYCModal ||
            showFarmerDetails ||
            showEmployeeDetails ||
            showChangePassword ||
            showChangeUserId
          ) && (
          <header className="dashboard-header">
            <div className="header-center">
              <div className="welcome-message">
                <h2 className="greeting">{getGreeting()}, {user?.name || user?.email || 'Employee'}! ðŸŽ‹</h2>
                <p className="welcome-text">Manage your assigned farmers and KYC verification tasks efficiently.</p>
              </div>
            </div>
          </header>
          )}

          {/* Change Password Form - Priority Display */}
          {showChangePassword && user && (
            <div className="dashboard-form-container">
              <ChangePasswordModal
                user={user}
                isOpen={true}
                isInDashboard={true}
                onClose={() => setShowChangePassword(false)}
                onSuccess={() => {
                  setNotification('Password changed successfully!');
                  setShowChangePassword(false);
                  setTimeout(() => setNotification(null), 3000);
                }}
              />
            </div>
          )}

          {/* Change User ID Form - Priority Display */}
          {showChangeUserId && user && (
            <div className="dashboard-form-container">
              <ChangeUserIdModal
                user={user}
                isOpen={true}
                isInDashboard={true}
                onClose={() => setShowChangeUserId(false)}
                onSuccess={() => {
                  setNotification('User ID changed successfully!');
                  setShowChangeUserId(false);
                  setTimeout(() => setNotification(null), 3000);
                }}
              />
            </div>
          )}

          {/* Render forms with priority over tab content */}
          {showFarmerForm ? (
            <div className="dashboard-form-container">
              <FarmerForm 
                isInDashboard={true}
                editData={editingFarmer}
                onClose={() => {
                  setShowFarmerForm(false);
                  setEditingFarmer(null);
                }}
                onSubmit={async (farmerData) => {
                  try {
                    // API call to create/update farmer
                    setAssignedFarmers(prev => [...prev, { ...farmerData, id: Date.now() }]);
                    setShowFarmerForm(false);
                    setEditingFarmer(null);
                    alert('Farmer saved successfully!');
                  } catch (error) {
                    console.error('Error saving farmer:', error);
                    alert('Failed to save farmer. Please try again.');
                  }
                }}
              />
            </div>
          ) : (
            <>
              {activeTab === 'overview' && renderOverview()}
              {activeTab === 'farmers' && renderAssignedFarmers()}
              {activeTab === 'progress' && renderKYCProgress()}
              {activeTab === 'todo' && renderTodoList()}
              {activeTab === 'kyc-summary' && renderKYCSummary()}
            </>
          )}

          {/* Notification Toast */}
          {notification && (
            <div className="notification-toast">
              {notification}
            </div>
          )}
        </div>
      </div>

      {/* Modals */}

      {showKYCModal && selectedFarmer && (
        <KYCModal
          farmer={selectedFarmer}
          onClose={() => {
            setShowKYCModal(false);
            setSelectedFarmer(null);
          }}
          onApprove={(farmerId, documents) => handleKYCUpdate(farmerId, 'APPROVED', '', documents)}
          onReject={(farmerId, reason) => handleKYCUpdate(farmerId, 'REJECTED', reason)}
          onReferBack={(farmerId, reason) => handleKYCUpdate(farmerId, 'REFER_BACK', reason)}
        />
      )}



      {showEmployeeDetails && selectedEmployeeData && (
        <ViewEditEmployeeDetails
          employee={selectedEmployeeData}
          onClose={handleCloseEmployeeDetails}
          onUpdate={handleUpdateEmployee}
        />
      )}


    </div>
  );
};

export default EmployeeDashboard;
